import L, { Icon } from "leaflet";
import "leaflet/dist/leaflet.css";
import React, { useState } from "react";
import {
  MapContainer,
  Marker,
  Popup,
  TileLayer,
  useMapEvent,
} from "react-leaflet";

const Map = () => {
  const hamiltonPosition = [43.2557, -79.8711]; // Hamilton, Ontario coordinates

  const [points, setPoints] = useState([]);
  // Custom icon options
  const customIcon = new Icon({
    iconUrl: require("./icon.png"),
    iconSize: [30, 30],
  });
  const handleContextMenu = (event) => {
    event.preventDefault(); // Prevent the default context menu

    // Add your custom functionality here
    console.log("Custom function triggered on right-click!");
  };

  const handleClick = (e) => {
    // Get the coordinates of the clicked location
    const { lat, lng } = e.latlng;

    // Set the coordinates in the state
    console.log(e);
    // Display the coordinates (you can replace this with your desired logic)
    alert(`Latitude: ${lat}\nLongitude: ${lng}`);
  };

  const LocationFind = () => {
    const map = useMapEvent({
      click(e) {
        console.log(e.latlng.lat);
        console.log(points);
        setPoints([...points, [e.latlng.lat, e.latlng.lng]]);
      },
    });
    return null;
  };

  const test = () => {
    const options = { method: "GET", headers: { accept: "application/json" } };

    fetch("https://us1.locationiq.com/v1/autocomplete", options)
      .then((response) => response.json())
      .then((response) => console.log(response))
      .catch((err) => console.error(err));
  };

  return (
    <div className="flex h-screen flex-col">
      <div className="flex justify-between bg-slate-100 px-10">
        <p className="text-2xl">Brians App</p>
        <div className="my-auto flex gap-5">
          <p onClick={test}>Menu</p>
          <p>Contact</p>
          <p>Help</p>
        </div>
      </div>
      <div className="relative w-full flex-1">
        <MapContainer
          center={hamiltonPosition}
          zoom={13}
          className="h-full w-full"
          //style={{ height: "100%", width: "100%" }}
          onClick={handleClick}
        >
          <LocationFind />
          <TileLayer
            url="https://{s}.tile.openstreetmap.fr/hot/{z}/{x}/{y}.png"
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          />
          {points.map((pos, index) => (
            <Marker key={index} position={pos} icon={customIcon}>
              <Popup>
                Hamilton, Ontario
                <br />
                Latitude: {pos[0]}
                <br />
                Longitude: {pos[1]}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
        <input
          className="w-50 absolute right-2 top-3 z-[10000] rounded px-2 py-1"
          placeholder="Search..."
        />
      </div>
    </div>
  );
};

export default Map;
