import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L, { Icon } from "leaflet";
import "leaflet/dist/leaflet.css";

const Map = () => {
  const hamiltonPosition = [43.2557, -79.8711]; // Hamilton, Ontario coordinates
  const pos2 = [43.252, -79.8711]; // Hamilton, Ontario coordinates
  const pos3 = [43.2556, -79.8711]; // Hamilton, Ontario coordinates
  const pos4 = [43.2557, -79.8711]; // Hamilton, Ontario coordinates

  // Custom icon options
  const customIcon = new Icon({
    iconUrl: require("./icon.png"),
    iconSize: [30, 30],
  });
  const handleContextMenu = (event) => {
    event.preventDefault(); // Prevent the default context menu

    // Add your custom functionality here
    console.log("Custom function triggered on right-click!");
  };

  const handleClick = (e) => {
    // Get the coordinates of the clicked location
    const { lat, lng } = e.latlng;

    // Set the coordinates in the state
    setCoordinates({ lat, lng });

    // Display the coordinates (you can replace this with your desired logic)
    alert(`Latitude: ${lat}\nLongitude: ${lng}`);
  };

  return (
    <div onContextMenu={handleContextMenu}>
      <div onContextMenu={handleContextMenu}>Right-click me!</div>

      <MapContainer
        center={hamiltonPosition}
        zoom={13}
        style={{ height: "400px", width: "100%" }}
        onClick={handleClick}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.fr/hot/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />

        <Marker position={hamiltonPosition} icon={customIcon}>
          <Popup>
            Hamilton, Ontario
            <br />
            Latitude: {hamiltonPosition[0]}
            <br />
            Longitude: {hamiltonPosition[1]}
          </Popup>
        </Marker>
        <Marker position={pos2} icon={customIcon}>
          <Popup>
            Hamilton, Ontario
            <br />
            Latitude: {hamiltonPosition[0]}
            <br />
            Longitude: {hamiltonPosition[1]}
          </Popup>
        </Marker>
        <Marker position={hamiltonPosition} icon={customIcon}>
          <Popup>
            Hamilton, Ontario
            <br />
            Latitude: {hamiltonPosition[0]}
            <br />
            Longitude: {hamiltonPosition[1]}
          </Popup>
        </Marker>
        <Marker position={hamiltonPosition} icon={customIcon}>
          <Popup>
            Hamilton, Ontario
            <br />
            Latitude: {hamiltonPosition[0]}
            <br />
            Longitude: {hamiltonPosition[1]}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default Map;
