// Map.js
import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import L from "leaflet"; // Add this line
import "leaflet/dist/leaflet.css";
import { CiLocationOn } from "react-icons/ci";
const Map = () => {
  const hamiltonPosition = [43.2557, -79.8711];

  // Custom icon options
  const customIcon = new L.Icon({
    iconUrl: "path-to-your-custom-icon.png", // Specify the path to your custom icon
    iconSize: [32, 32], // Size of the icon
    iconAnchor: [16, 32], // Anchor point of the icon
    popupAnchor: [0, -32], // Popup anchor point
  });

  return (
    <div>
      <CiLocationOn />
      <MapContainer
        center={hamiltonPosition}
        zoom={13}
        style={{ height: "400px", width: "100%" }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        <Marker position={hamiltonPosition} icon={CiLocationOn}>
          <Popup>
            Hamilton, Ontario
            <br />
            Latitude: {hamiltonPosition[0]}
            <br />
            Longitude: {hamiltonPosition[1]}
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};

export default Map;
